class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] diff = new int[2 * n - 1];
        for (int i = 1; i < n; i++) {
        	diff[i - 1] =  colors[i - 1] == colors[i] ? 1 : 0;
        	diff[i - 1 + n] = diff[i - 1];
        }
        diff[n - 1] = colors[n - 1] == colors[0] ? 1 : 0;
        int sum = 0;
        for (int i = 0; i < k - 1; i++)
        	sum += diff[i];
        int result = sum == 0 ? 1 : 0;
        for (int i = 1; i < n; i++) {
        	sum += diff[i + k - 2] - diff[i - 1];
        	result += (sum == 0 ? 1 : 0);
        }
        return result;
    }
}
