class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& aa, int k) {
        int n=aa.size();
        int o=0;
        int z=0;
        int ans=0;
        vector<int>a;
        for(int i=0;i<n;i++){
            a.push_back(aa[i]);
        }
        for(int i=0;i<n;i++){
            a.push_back(aa[i]);
        }
        int count=1;
        for(int i=1;i<(n+k)-1;){
            if(a[i]!=a[i-1]){
                count++;
                i++;
            }
            else{
                ans+=(max(0,count-k+1));
                count=1;
                i++;
            }
        }
        ans+=max(0,count-k+1);
        return ans;
    }
};
