class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int last = -1;
        int ans = 0;
        int n = c.size();
        for(int i=0; i<n; i++){
            if (i > 0 && c[i] == c[i-1]) last = i-1;
            if (i + 1 >= k && (last <= (i-k))){
                 ans++;
            }
        }
        for(int i=0; i<k-1; i++){
            int prev = (i - 1 + n) % n;
            if (c[i] == c[prev]) last = prev;
            if ((last >= i && last <= ((i-k+n) % n)) || (last==-1)){
                 ans++;
            }
            // cout << last << " " << ans << endl;
        }
        return ans;
    }
};
