class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int[] copy = new int[colors.length + k - 1];
        System.arraycopy(colors, 0, copy, 0, colors.length);
        System.arraycopy(colors, 0, copy, colors.length, copy.length - colors.length);
        int[] prefix = new int[copy.length];
        for (int i = 1; i < copy.length; i++) {
            prefix[i] = prefix[i-1];
            if (copy[i] != copy[i-1]) prefix[i]++;
        }
        int count = 0;
        for (int i = 0; i < colors.length; i++) {
            if (prefix[i+k-1] - prefix[i] == k - 1) count++;
        }
        return count;
    }
}
