class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0, first = 0, last = 0, pre = colors[0];
        for(int i = 0; i < k - 1; i++) colors.push_back(colors[i]);
        int sz = colors.size();
        for(int i = 1; i < sz ; i++){
            if(colors[i] == pre){
                ans += max(0, (last - first  - k + 2));
                first = last = i;
                while(i < sz && colors[i] == pre){
                    i++;
                }
                i--;
                first = last = i;
            }
            last = i;
            pre = colors[i];
        }
        ans += max(0, (last - first - k + 2));
        return ans;
    }
};
