class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        const int n = (int)a.size();
        vector<int> b = a;
        for (auto& e: a)
            b.push_back(e);
        const int m = (int)b.size();
        vector<int> p(m, 0);
        for (int i = 0; i < m; i++) {
            if (i + 1 < m and b[i] != b[i + 1]) {
                p[i] = 1;
            } 
            if (i - 1 >= 0) {
                p[i] += p[i - 1];
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            int l = 0, r = 0;
            if (i - 1 >= 0) {
                l = p[i - 1];
            } 
            r = p[i + k - 2];
            if (r - l == k - 1) 
                ans++;
        }
        return ans;
    }
};
