class Solution{
public:
    int numberOfAlternatingGroups(vector<int> &c, int k){
        int n = c.size(); 

        int res = 0; 
        if(k == 1)  
            return n; 

        int curr=1; 
        int j=1; 
        while(j<n+k-1){ 
            int i=j-1;
            if(c[j%n] != c[i%n]){ 
                ++curr; 
            } else { 
                curr=1; 
            } 

            if (curr>=k){ 
                ++res; 
            } 

            j++; 
        } 

        return res;

    }
};
