class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for( int i = 0; i < k - 1; ++i ) {
            colors.push_back( colors[ i ] );
        }
        int ans = 0;
        int idx = 0;
        deque<int> vals;
        while( idx < colors.size() ) {
            if( !vals.empty() && colors[ idx ] == vals.back() ) {
                vals.clear();
            }
            vals.push_back( colors[ idx ] );
            if( vals.size() > k ) {
                vals.pop_front();
            }
            if( vals.size() == k ) {
                ++ans;
            }
            ++idx;
        }

        return ans;
    }
};
