class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i=0;
        int j=1;
        int ct=1;
        int ans=0;
        int n=colors.size();
        while(i<n){
            int p=j%n;
             if(colors[p] != colors[(p-1+n)%n]){
                ct++;
                if(ct>=k)ans++;
                j++;
             }else{
                i=j;
                j++;
                ct=1;
             }

            if(j-i>=k)i++;
        }
        return ans;
    }
};
