class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
         int c=-1;
        int n=v.size();
        int ans=0;
        int cur=0;
        for(int i=n-1;i>=0;i--){
            if(v[i]!=c)cur++;
            else cur=1;
            c=v[i];
            if(cur>=k)ans++;
        }
        if(v[0]==v[n-1])return ans;
        int l=0;
        int r=0;
        c=v[0];
       int i=1;
        while(i<n-1 and v[i]!=c){
            l++;
            c=v[i++];
        }
        i=n-2;
        c=v[n-1];
        while(i>0 and v[i]!=c){
            r++;
            c=v[i--];
        }
        for(int i=k-2;i>=0;i--){
            if(r>=(k-2-i) and l>=i)ans++;
        }
        return ans;
    }
};
