class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        const auto endIt = colors.begin() + k - 1;
        colors.insert(colors.end(), colors.begin(), endIt);

        const int n = colors.size();
        int ans = 0, size = 1;

        for (int j = 1; j < n; ++j) {
            if (colors[j] != colors[j - 1]) {
                if (++size >= k) {
                    ++ans;
                }
            } else {
                size = 1;
            }
        }
        return ans;
    }
};
