class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        A = colors

        for i in range(k-1):
            A.append(colors[i])

        res = []
        temp = 1
        cnt = 0

        for i in range(1, len(A)):
            if A[i] == A[i-1]:
                res.append(temp)
                temp = 1
            else:
                temp += 1

        res.append(temp)

        for i in res:
            if i >= k:
                cnt += i - k + 1

        return cnt
