class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        left = 0
        n = len(colors)
        ans = 0
        for right in range(1, n+k-1):
            cur = right % len(colors)
            prev = (right - 1) % len(colors)
            if colors[cur] == colors[prev]:
                left = right
            elif right - left == k - 1:
                ans += 1
            elif right - left == k:
                ans += 1
                left += 1

        return ans
