class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int p = n+k-1;
        vector<int> colors1(p);
        for(int i=0;i<n;i++){
            colors1[i] = colors[i];
        }
        for(int i=n;i<p;i++){
            colors1[i] = colors[i-n];
        }

        vector<int> pref(p);
        int cnt = 1;
        pref[0]=1;
        for(int i=1;i<p;i++){
            if(colors1[i]!=colors1[i-1]){
                cnt++;

            }
            else{
                cnt = 1;
            }

            pref[i] = cnt;
        }
        int ans;
        for(int i=0;i<p;i++){
            if(pref[i]>=k) ans++;
        }

        return ans;

    }
};
