class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        if(k==1){
            return colors.length;
        }
      int n=colors.length;
      int ans=0;

        int len=1;
        int i=1;
        while(i<n+k-1){
            int currIdx=i;
            int prevIdx=i-1;

            if(currIdx>=n){
                currIdx=currIdx-n;
            }
            if(prevIdx>=n){
                prevIdx=prevIdx-n;
            }

            int currEle=colors[currIdx];
            int prevEle=colors[prevIdx];

            if(currEle!=prevEle){
                len++;
                }
            else{
                len=1;
            }

            if(len>=k){
                ans++;
            }
            i++;

        }
        return ans;
    }
}
