class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        now = -1
        s = -1
        ans = 0
        for i, x in enumerate(colors + colors[:k - 1]):
            if x != now:
                now = x
                if i - s >= k:
                    ans += 1
            else:
                now = x
                s = i - 1

        return ans
