class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int count = 0;
        int start = 0;
        int end = 1;

        while(end < colors.length + k && start < colors.length) {
            int tempEnd = end;
            int tempEndMod = tempEnd % colors.length;

            while(colors[tempEndMod] != colors[(tempEndMod - 1 + colors.length) % colors.length] && (tempEnd < (colors.length + k - 1))) {
                tempEnd++;
                tempEndMod = tempEnd % colors.length;
            }

            count += Math.max(tempEnd - start - k + 1, 0);
            start = tempEnd;
            end = start + 1;
        }

        return count;
    }
}
