/**
 * @param {number[]} colors
 * @param {number} k
 * @return {number}
 */

function solve(alter_len, c, p, colors, k, ans) {
     let n = colors.length;
    let i = 1;
    while (i < n + k - 1) {
        let c = i;
        let p = i - 1;

        if (c >= n) {
            c -= n;   //range mein lao
        }
        if (p >= n) {  
            p -= n;   //range mein lao
        }

        if (colors[c] !== colors[p]) {
            ++alter_len;
        } else {
            alter_len = 1; // set as 1
        }

        if (alter_len >= k) {
            ++ans;
        }

        ++i;
    }

    return ans;
}
var numberOfAlternatingGroups = function(colors, k) {
    let n = colors.length;
    let ans = 0;

    if (k === 1) { // edge case
        return n;
    }

    let alter_len = 1;
    let i = 1;
    ans = solve(alter_len, i, i - 1, colors, k, ans);
    return ans;
};
