class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> v; 
        v = colors;
        for(int i=0; i<k-1; i++){
            v.push_back(colors[i]);
        }
        int i=0;
        int j=1;
        int count=1;
        int ans=0;
        while(i<v.size() and j<v.size()){
            if(count==k-1){
                if(v[j]!=v[j-1]){
                    ans++;
                    i++;
                    j++;
                }else{
                    i=j;
                    j++;
                    count=1;
                }
            }else{
                if(v[j]!=v[j-1]){
                    count++;
                    j++;
                }else{
                    i=j;
                    j++;
                    count=1;
                }
            }
        }
        return ans;
    }
};
