class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans=0;
        int prev=-1;
        int c=colors.size();
        for(int i=0;i<c;i++) colors.push_back(colors[i]);
        int n=colors.size();
        for(int i=1;i<c+k-1;i++)
        {
              if(i>=k-1 && colors[i]!=colors[i-1] && prev<=i-k+1) ans++;
              if(colors[i]==colors[i-1]) prev=i;
        }
         return ans;
    }
};
