class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        if (k == 1){
            return n;
        }
        if (k > n){
            return 0;
        }
        vector<int> v(n);
        for (int i = 0; i < n; ++i) {
            v[i] = (colors[i] != colors[(i+1)%n]);
        }
        int sum = 0;
        for (int i=0; i<k-1; ++i) {
            sum += v[i];
        }
        int count = 0;
        for (int i=0; i<n; ++i) {
            if (sum==(k-1)) {
                count++;
            }
            sum -= v[i];
            sum += v[(k+i-1)%n];
        }

        return count;
    }
};
