class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int ans = 0;
        auto checkEnd = [&](int en, int tend) {
            return arr[tend] != arr[(tend - 1 + arr.size()) % arr.size()] && en < arr.size() + k - 1;
        };
        int st = 0, en = 1;
        while (en < arr.size() + k && st < arr.size()) {
            int tj = en;
            int tend = tj % arr.size();
            while (checkEnd(tj, tend))
                tj++, tend = tj % arr.size();
            ans += max(tj - st - k + 1, 0);
            st = tj,  en = st + 1;
        }
        return ans;
    }
};
