class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        if(k > n) return 0;
        int res = 0;
        int idx = 0;
        while(idx < n) {
            int right = idx + 1;
            while(right < n + k - 1 && colors[right % n] != colors[(right - 1) % n]) right ++;
            if(right - idx >= k) {
                res += right - idx - k + 1;
                idx = right;
            }else {
                idx ++;
            }
        }
        return res;
    }
}
