class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        int n = colors.size();
        int cnt = 0;

        if(k == 1)
            return n;

        int altlen = 1;
        int i=1;
        while(i < n+k-1)
        {
            int curr = i;
            int prev = i-1;

            if(curr >= n)
                curr %= n;
            if(prev >= n)
                prev %= n;

            if(colors[curr]!=colors[prev])
                altlen++;
            else
                altlen = 1;

            if(altlen >= k)
                cnt++;

            i++;
        }
        return cnt;
    }
};
