class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        long long c = 0;

        int n = colors.size();
         int left = 0;
        int right = 1;

        while (right < n + k && left < n) {
            int x = right;
            int y =x % n;
//  cout<<x<<y
            while ( x < n + k - 1 &&  colors[y] != colors[(y - 1 + n) % n]) {
                 x++;
                y =x % n;
            }

          // cout<< y
            c += max(0,x + 1 - k-left);
            left = x;
            right = left + 1;
        }

        return c;

    }
};
