from typing import List


class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        colors+=colors
        dp=[0]*(n+n+1)
        for i in range(n*2-1):
            if colors[i]!=colors[i+1]:
                dp[i]=1
        for i in range(1,n*2):
            dp[i]+=dp[i-1]

        res=0
        for i in range(n):
            v=dp[i+k-2]-(0 if i==0 else dp[i-1])
            if v==k-1:
                res+=1
        return res
