// TC->O(N)
// SC->O(1)
class Solution { 
public: 
    int numberOfAlternatingGroups(vector<int>& colors, int k) { 
        int n=colors.size(); 
        int cnt = 0; 

        if (k==1) { 
            return n;
        } 

        int len=1; 
        int i=1;

        //sliding window
        while (i<n+k-1) { 
            int curr = i,prev=i-1; 

            if (curr >= n) { 
                curr=curr-n; 
            } 
            if (prev>=n) { 
                prev= prev-n; 
            } 

            if (colors[curr] != colors[prev])
                len++; 
            else
                len=1; 
            if (len>=k)
                cnt++; 
            i++;
        } 

        return cnt; 
    } 
};
