class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        colors.insert(colors.end(),colors.begin(),colors.end());
        int n = colors.size();
        vector<int> prefix(n);
        for(int i = 1;i<n-1;i++){
            if(colors[i]!=colors[i-1] && colors[i]!=colors[i+1])
                prefix[i] = prefix[i-1]+1;
            else
                prefix[i] = prefix[i-1];
        }
        int ans = 0;
        for(int i = k-1;i<n/2+k-1;i++){
            if(prefix[i-1]-prefix[i-k+1]==k-2)
                ans++;
        }
        return ans;

    }
};
