#include <vector>
using namespace std;

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int i{},j{1},res{};
        int n = colors.size();
        int ans =0ll;
        while (i<=n-1 && j < (n + k)) {
            int t1 = j, y = t1%n;
            while (colors[y] != colors[(y+n-1) % n] && t1 < n-1+k) {
                t1++;
                y = t1 % n;
            }
            res += max(t1-(i+k) + 1 * 1ll, 0ll);
            i = max(ans, t1),j = max(ans, i+1);
        }
        return res;
    }
};
