class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n=arr.size();
        vector<int>vec;
        for(int i=0;i<n;i++){
            vec.push_back(arr[i]);
        }
        for(int i=0;i<k-1;i++){
            vec.push_back(arr[i]);
        }
        int answer=0;
        n=vec.size();
        for(int i=0;i<n;i++){
            int cnt=1;
            for(int j=i+1;j<n;j++){
                if(vec[j]!=vec[j-1]){
                    cnt++;
                }
                else{
                    i=j-1;
                    break;
                }
                if(j==n-1){
                    i=3*(n);
                    break;
                }
            }
            answer+=max(0,cnt-k+1);
        }
        return answer;

    }
};
