class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        vector<int>pre(n*2);
        for(int i=0;i<2*n;i++){
            int idx1=i%n,idx2=(i+1)%n;
            pre[i]=(i?pre[i-1]:0)+(v[idx1]==v[idx2]);
        }
        int ans=0;
        for(int i=0;i<n;i++){
            int l=i,r=i+k-2;
            if(pre[r]-(l?pre[l-1]:0))continue;
            ans++;
        }
        return ans;
    }
};
