class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), ans = 0;
        vector<int> v(n * 2), s(n * 2, 0);
        for (int i = 0; i < n * 2; i ++ ) {
            v[i] = colors[i % n];
            if (i) s[i] = s[i - 1] + (v[i] ^ v[i - 1]);
        }
        for (int i = 0; i < n; i ++ ) {
            int l = i, r = i + k - 1;
            if (s[r] - s[l] == k - 1) ans++;
        }
        return ans;
    }
};
