class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
             int n = colors.length; 
            int count = 0; 
            if (k == 1) { 
                return n; 
            } 
            int alternate = 1; 
            int i = 1; 
            while (i < n + k - 1) { 
                int comm = i; 
                int pos = i - 1; 

                if (comm >= n) { 
                    comm -= n; 
                } 
                if (pos>= n) { 
                    pos -= n; 
                } 

                if (colors[comm] != colors[pos]) { 
                    ++alternate; 
                } else { 
                    alternate = 1; 
                } 

                if (alternate >= k) { 
                    ++count; 
                } 

                ++i; 
            }
        return count;
    }
}
