class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        vector<int> v=col;
        int n=col.size(),ans=0;

        for(int i=0;i<n;i++){
            v.push_back(col[i]);
        }

        int l=0,r=1;

        while(r<n+k-1){
            if(v[r]==v[r-1]) l=r;

            if(r-l+1>=k) ans++;

            r++;
        }

        return ans;
    }
};
