class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {

        int n = colors.size(),i=0,j=1,ans=0;

        while ( i < n and j-k < n) {
            int curr = j, end = curr % n;

            while (colors[end] != colors[(end - 1 + n) % n] && curr <= (n + k - 2)) {
                curr++;
                end = curr % n;
            }
             ans += max((curr - i - k + 1), 0);
           j = i + 1,   i = curr;

        }

        return ans;
    }
};
