class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& sequence, int minLength) {
       for (int i = 0; i < minLength - 1; i++) {
            sequence.push_back(sequence[i]);
        }
        int groupLength = 1;
        vector<int> groups;
        for (int i = 1; i < sequence.size(); i++) {
            if (sequence[i] == sequence[i - 1]) {
                groups.push_back(groupLength);
                groupLength = 1;
            } else {
                ++groupLength;
            }
        }
        groups.push_back(groupLength);
        int result = 0;
        for (auto length : groups) {
            if (length >= minLength) {
                result += length - minLength + 1;
            }
        }
        return result;
    }
};
