class Solution
{
public:
    int numberOfAlternatingGroups(vector<int> &colors, int k)
    {
        int n = colors.size();
        int ans = 0;
        int i = 0;
        bool expected = colors[0];
        for (int j = 0; j < n; j++)
        {
            if (expected != colors[j])
            {
                i = j;
                expected = colors[j];
            }

            if (j - i == k - 1)
            {
                ans++;
                // cout << i << " " << j << endl;
                i++;
                // return 0;
            }

            expected = !expected;
        }

        vector<int> temp;

        for (int j = n - k + 1; j < n; j++)
            temp.push_back(colors[j]);

        for (int j = 0; j < k - 1; j++)
            temp.push_back(colors[j]);

        i = 0;
        expected = temp[0];
        for (int j = 0; j < temp.size(); j++)
        {
            if (expected != temp[j])
            {
                i = j;
                expected = temp[j];
            }

            if (j - i == k - 1)
            {
                ans++;
                // cout << i << " " << j << endl;
                i++;
                // return 0;
            }

            expected = !expected;
        }

        return ans;
    }
};
