class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        for (int i = 0; i < k - 1; ++i) {
            colors.push_back(colors[i]);
        }
        const int n = colors.size();
        for (int i = 0; i + k - 1 < n; ++i) {
            int find = colors[i];
            int j = i;
            while (j < n && colors[j] == find) {
                j++;
                find ^= 1;
            }
            int len = j - i;
            ans += max(0, len - k + 1);
            i = j - 1;
        }
        return ans;
    }
};
