class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>vec=colors;

        int n=colors.size();
        int cnt=0;
        if(k==1) return n;

        int x=1;
        int i=1;
        while(i<n+k-1){
            int c=i;
            int p=i-1;
            if(c>=n) c-=n;
            if(p>=n) p-=n;

            if(colors[c]!=colors[p]) ++x;
            else x=1;

            if(x>=k) cnt++;

            i++;
        }
        return cnt;

    }
};
