class Solution {
public:
    int n;
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        n = colors.size();
        vector<int> v = colors;
        for(int i = 0; i < n; i++)  v.push_back(colors[i]);
        int cnt = 0;

        int i = 0, j = 1;
        int ans = 0;

        // cout << ": ";
//         
        while(i < n)
        {
            if(v[j] == v[j-1])
            {
                // cout << j << " ";
                i = j;
                j++;
                continue;
            }

            if(j-i+1 < k)
            {
                j++;
                continue;
            }

            else{
                i++;
                j++;
                ans++;
            }
        }
        // cout << endl;

        return ans;
    }
};
