class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& cr, int k) {
        int myans = 0;
        int i = 0;
        int j = 1;
        int n = cr.size();
        while (j < n + k && i < n) {
            int dommy = j;
            int dommyend = dommy % n;
            while (cr[dommyend] != cr[(dommyend - 1 + n) % n] &&
                   dommy < n + k - 1) {
                dommy++;
                dommyend = dommy % n;
            }
            myans += max(dommy - i - k + 1, 0);
            i = dommy;
            j = i + 1;
        }
        return myans;
    }
};
