class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


        int all_cnt = 0; 
        int size = colors.size(); 

        if (k == 1) { 
            return size; 
        } 

        int optional = 1; 
        int i = 1; 
        while (i < size + k - 1) { 
            int first = i; 
            int past = i - 1; 

            if (first >= size) { 
                first = first - size; 
            } 
            if (past >= size) { 
                past = past- size; 
            } 

            if (colors[first] != colors[past]) { 
                ++optional; 
            } else { 
                optional = 1; 
            } 

            if (optional >= k) { 
                ++all_cnt; 
            } 

            ++i; 
        } 

        return all_cnt; 


    }
};
