class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        for(int i=0;i<k-1;i++) a.push_back(a[i]);
        int n = a.size(), ans = 0;
        for(int i=0;i<n;i++){
            int j = i+1;
            while(j<n && a[j]!=a[j-1]) j++;
            j--;
            if(j-i+1>=k)
            ans+=(j-i+1)-k+1;
            i = j;
        }
        return ans;
    }
};
