class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& col, int k) {
        int n=col.size();

        int ans=0;

        int len=1;
        for(int i=1;i<n+k-1;i++){
            if(col[0]==col[n-1] && i>=n)
                break;
            int idx=i%n;

            int prev=idx-1;
            if(prev==-1)
                prev=n-1;
            if(col[idx]!=col[prev])
                len++;
            else{
                if(len-k>=0)
                    ans+=len-k+1;
                len=1;
            }
        }
        if(len-k>=0)
            ans+=len-k+1;

        return ans;
    }
};
