class Solution:
    def numberOfAlternatingGroups(self, arr: List[int], k: int) -> int:
            ans=0
            cur=1
            n=len(arr)
            for i in range(1,len(arr)+k-1):
                if arr[i%n]!=arr[(i-1)%n]:
                    cur+=1
                else:
                    cur=1
                if cur>=k:
                    ans+=1
                    cur-=1
            return ans
