class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> v(c.begin(),c.end());
        int n=c.size();
        for(int i=0;i<n;i++){
            v.push_back(c[i]);
        }
        int i=0,j=0,ans=0;
        int lok=v[0];
        for(int j=0;j<2*n;j++){
            // cout<<j<<' '<<i<<' '<<lok<<' '<< ans<<endl;
            if(lok!=v[j]){
                i=j; 
                lok=v[j];
            }
            if(k==j-i+1){
                ans++;
                i++;
            }
            lok=1-v[j];
            if(i>=n)break;
        }
        return ans;
    }   
};
