class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;

        if(k==1)
            return n;

        int altGrps = 0;
        int len = 0;

        for(int i = 1; i<=n+k-1; i++) {
            int curr = i%n;
            int prev = (i-1)%n;

            if (colors[curr] != colors[prev]) {
                len++;
            }
            else {
                len = 1;
            }

            if (len >= k)
                altGrps++;
        }

        return altGrps;
    }
}
