class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int cnt = 0,ans = 0;
        int n=c.size();
        for(int i=1;i<n;i++){
            if(c[i-1]!=c[i] && c[(i+1)%n]!=c[i]){
                cnt++;
                if(cnt == k-2){
                    ans++;
                    cnt--;
                }
            }

            else cnt = 0;
        }

        for(int i=0;i<k-2;i++){
            if(i == 0){
                if(c[n-1]!=c[i] && c[i+1]!=c[i]){
                    cnt++;
                if(cnt == k-2){
                    ans++;
                    cnt--;
                  }
                }
            }

            else if(c[i-1]!=c[i] && c[(i+1)%n]!=c[i]){
                cnt++;
                if(cnt == k-2){
                    ans++;
                    cnt--;
                }
            }

            else cnt = 0;
        }

        return ans;
    }
};
