class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for (int i = 0; i < k - 1; i++) {
            colors.push_back(colors[i]);
        }
        for (int i = colors.size() - 1; i > 0; i--) {
            colors[i] = (colors[i] != colors[i - 1]);
        }
        colors[0] = 0; k--;
        int ret = 0;
        int c = 0;
        for (int i = 1; i < colors.size(); i++) {
            if (colors[i] == 0) {
                c = 0;
            }
            else {
                ++c;
                if (c >= k) {
                    ++ret;
                }
            }
        }
        return ret;
    }
};
