class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        q = deque([])
        ans = 0
        for i in range(k - 1):
            if colors[i] == colors[i + 1]:
                q.append(i)
        for i in range(n):
            if not q:
                ans += 1
            if q and q[0] == i:
                q.popleft()
            if colors[(i + k) % n] == colors[(i + k - 1) % n]:
                q.append((i + k - 1) % n)
        return ans
